import Layout from "../components/Layout";
import Link from "next/link";
import { withRouter } from "next/router";

// 'as' prop of Link tag will replace the url name with the string value passed to 'as' so that we can pass something as query parameter and 
// it will not be visible in the url.
const PostLink = ({ slug, title, pathName }) => (
	<li>
		<Link as = {`/${slug}`} href = {`/post?title=${title}`}>
			<a> { title }</a>
		</Link>
	</li>
);

const Blog = ({ router }) => 
{ 
	return (
		<Layout title="My Blog">
			<ul>
				<PostLink pathName = { router.pathname } slug="react-post" title="React Post" />
				<PostLink pathName = { router.pathname } slug="angular-post" title="Angular Post" />
				<PostLink pathName = { router.pathname } slug="vue-post" title="Vue Post" />
			</ul>
		</Layout>
	);
}

export default withRouter(Blog);
